    def set_input_listeners(self) -> None:
        
        self.horizontalSlider_8.valueChanged.connect(self.__set_arm_vel)
        self.horizontalSlider_6.valueChanged.connect(self.__set_crab_vel)
        self.horizontalSlider_7.valueChanged.connect(self.__set_hoist_angular_vel)
        self.horizontalSlider_5.valueChanged.connect(self.__set_hoist_vertical_vel)
        
        self.pushButton_7.clicked.connect(self.__start_simulation)
        self.pushButton_6.clicked.connect(self.__stop_simulation)
        self.pushButton_10.clicked.connect(self.__turn_magnet_on)
        self.pushButton_9.clicked.connect(self.__turn_magnet_off)

    def __start_simulation(self) -> None:
        self.__controller.start_simulation()

    def __stop_simulation(self) -> None:
        self.__controller.stop_simulation()

