simulation: Simulation = None
app: QtWidgets.QApplication = None

try:

    # Captura IP customizado (se houver)
    ip = sys.argv[1] if (len(sys.argv) > 1) else None
    if (len(sys.argv) > 1):
        ip = sys.argv[1]

    # Inicia simulacao
    simulation = Simulation()
    is_connected, client_id, conn_error = simulation.connect(ip) if ip else simulation.connect()
    if (not is_connected):
        raise ConnectionError(conn_error)

    # Iniciar controlador & tela
    app = QtWidgets.QApplication(sys.argv)
    
    window = QtWidgets.QMainWindow()
    controller = Controller(simulation)
    ui = Ui_MainWindow()
    ui.setupUi(window, controller)
    ui.set_input_listeners()
    
    window.show()
    sys.exit(app.exec_())
            
except ConnectionAbortedError as conn_error:
    print('\nFailure as trying to start simulation: ', conn_error)
    raise conn_error

except ConnectionError as conn_error:
    print('\nLost connestr()ction to simulation: ', conn_error)
    raise conn_error

except KeyboardInterrupt:
    pass

except Exception as error:
    print('\nFalha inesperada: ', error)
    raise error

finally:
    print('\n-- THE END --\n')
    if simulation:
        simulation.disconnect()
    
